mapper.select:
SELECT * FROM product WHERE id IN

    #{id}

AND status = 1

mapper.update:
UPDATE product
name = #{name}, description = #{description}, category_id = #{categoryId}, price = #{price}, original_price = #{originalPrice}, stock = #{stock}, sales = #{sales}, main_image = #{mainImage}, images = #{images}, status = #{status},
WHERE id = #{id}

UPDATE product 
SET main_image = #{mainImage}, images = #{images}
WHERE id = #{id}
